o-top:hover {\n  background: #151515;\n}\n\n.back-to-top:hover i {\n  color: #ffc451;\n}\n\n.back-to-top.active {\n  visibility: visible;\n  opacity: 1;\n}\n\n\n#preloader {\n  position: fixed;\n  top: 0;\n  left: 0;\n  right: 0;\n  bottom: 0;\n  z-index: 9999;\n  overflow: hidden;\n  background: #151515;\n}\n\n#preloader:before {\n  content: \"\";\n  position: fixed;\n  top: calc(50% - 0px);\n  left: calc(50% - 30px);\n  border: 6px solid #ffc451;\n  border-top-color: #151515;\n  border-bottom-color: #151515;\n  border-radius: 50%;\n  width: 60px;\n  height: 60px;\n  animation: animate-preloader 1s linear infinite;\n}\n\n@keyframes animate-preloader {\n  0% {\n    transform: rotate(0deg);\n  }\n\n  100% {\n    transform: rotate(360deg);\n  }\n}\n\n\n@media screen and (max-width: 768px) {\n  [data-aos-delay] {\n    transition-delay: 0 !important;\n  }\n}\n\n\n#header {\n  transition: all 0.5s;\n  z-index: 997;\n  padding: 15px 0;\n}\n\n#header.header-scrolled,\n#header.header-inner-pages {\n  background: rgba(0, 0, 0, 0.8);\n}\n\n#header .logo {\n  font-size: 32px;\n  margin: 0;\n  padding: 0;\n  line-height: 1;\n  font-weight: 700;\n  letter-spacing: 2px;\n  text-transform: uppercase;\n}\n\n#header .logo a {\n  color: #fff;\n}\n\n#header .logo a span {\n  color: #ffc451;\n}\n\n#header .logo img {\n  max-height: 40px;\n}\n\n\n.get-started-btn {\n  color: #fff;\n  border-radius: 4px;\n  padding: 7px 25px 8px 25px;\n  white-space: nowrap;\n  transition: 0.3s;\n  font-size: 14px;\n  display: inline-block;\n  border: 2px solid #ffc451;\n}\n\n.get-started-btn:hover {\n  background: #ffbb38;\n  color: #343a40;\n}\n\n@media (max-width: 992px) {\n  .get-started-btn {\n    padding: 7px 20px 8px 20px;\n    margin-right: 15px;\n  }\n}\n\n\n\n.navbar {\n  padding: 0;\n}\n\n.navbar ul {\n  margin: 0;\n  padding: 0;\n  display: flex;\n  list-style: none;\n  align-items: center;\n}\n\n.navbar li {\n  position: relative;\n}\n\n.navbar a,\n.navbar a:focus {\n  display: flex;\n  align-items: center;\n  justify-content: space-between;\n  padding: 10px 0 10px 30px;\n  font-size: 15px;\n  font-weight: 600;\n  color: #fff;\n  white-space: nowrap;\n  transition: 0.3s;\n}\n\n.navbar a i,\n.navbar a:focus i {\n  font-size: 12px;\n  line-height: 0;\n  margin-left: 5px;\n}\n\n.navbar a:hover,\n.navbar .active,\n.navbar .active:focus,\n.navbar li:hover>a {\n  color: #ffc451;\n}\n\n.navbar .dropdown ul {\n  display: block;\n  position: absolute;\n  left: 14px;\n  top: calc(100% + 30px);\n  margin: 0;\n  padding: 10px 0;\n  z-index: 99;\n  opacity: 0;\n  visibility: hidden;\n  background: #fff;\n  box-shadow: 0px 0px 30px rgba(127, 137, 161, 0.25);\n  transition: 0.3s;\n}\n\n.navbar .dropdown ul li {\n  min-width: 200px;\n}\n\n.navbar .dropdown ul a {\n  padding: 10px 20px;\n  font-size: 14px;\n  text-transform: none;\n  color: #151515;\n  font-weight: 400;\n}\n\n.navbar .dropdown ul a i {\n  font-size: 12px;\n}\n\n.navbar .dropdown ul a:hover,\n.navbar .dropdown ul .active:hover,\n.navbar .dropdown ul li:hover>a {\n  background-color: #ffc451;\n}\n\n.navbar .dropdown:hover>ul {\n  opacity: 1;\n  top: 100%;\n  visibility: visible;\n}\n\n.navbar .dropdown .dropdown ul {\n  top: 0;\n  left: calc(100% - 30px);\n  visibility: hidden;\n}\n\n.navbar .dropdown .dropdown:hover>ul {\n  opacity: 1;\n  top: 0;\n  left: 100%;\n  visibility: visible;\n}\n\n@media (max-width: 1366px) {\n  .navbar .dropdown .dropdown ul {\n    left: -90%;\n  }\n\n  .navbar .dropdown .dropdown:hover>ul {\n    left: -100%;\n  }\n}\n\n\n.mobile-nav-toggle {\n  color: #fff;\n  font-size: 28px;\n  cursor: pointer;\n  display: none;\n  line-height: 0;\n  transition: 0.5s;\n}\n\n@media (max-width: 991px) {\n  .mobile-nav-toggle {\n    display: block;\n  }\n\n  .navbar ul {\n    display: none;\n  }\n}\n\n.navbar-mobile {\n  position: fixed;\n  overflow: hidden;\n  top: 0;\n  right: 0;\n  left: 0;\n  bottom: 0;\n  background: rgba(0, 0, 0, 0.9);\n  transition: 0.3s;\n  z-index: 999;\n}\n\n.navbar